package numbers;

import java.util.Scanner;

public class DataTypes {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		
		//DataTypes 
		byte b = -128; 
		short s = 32757; 
		int num = 1234567890; 
		long l = 876; 
		float f = 3.14f; 
		double d = 5e3; 
		boolean status = true; 
		
		
		
		//printing all data types with one print statement, using \n to separate into new lines 
		System.out.print("Byte Value: " + b +"\n" + "Short Value: " + s + "\n" + "Int Value: " + num + "\n" + "Long Value: " + l + "\n" 
				+ "Float Value: " + f + "\n" + "Double Value: " + d + "\n" + "Boolean Value: " + status + "\n"); 
		
		
				
		
		
	}

}
